import React, { useState, useMemo } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const months = [
  "Jan", "Feb", "Mar", "Apr", "May", "Jun",
  "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"
];

function StudentForm() {
  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    studentId: "",
    education: "",
    major: "",
    degreeStart: { month: 0, year: 2021 },
    degreeEnd: { month: 0, year: 2024 },
    gender: "",
  });

  const [response, setResponse] = useState("");

  // Function to move month forward/backward
  const changeMonth = (field, direction) => {
    setForm((prev) => {
      let { month, year } = prev[field];
      month += direction;
      if (month < 0) {
        month = 11;
        year -= 1;
      } else if (month > 11) {
        month = 0;
        year += 1;
      }
      return { ...prev, [field]: { month, year } };
    });
  };

  // Calculate duration between start and end
  const durationText = useMemo(() => {
    const start = form.degreeStart;
    const end = form.degreeEnd;
    const totalMonths = (end.year - start.year) * 12 + (end.month - start.month);
    if (totalMonths < 0) return "";
    const years = Math.floor(totalMonths / 12);
    const months = totalMonths % 12;
    return `${years > 0 ? years + " year(s) " : ""}${months > 0 ? months + " month(s)" : ""}`;
  }, [form.degreeStart, form.degreeEnd]);

  // Handle normal field changes
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Format degree data before sending
      const payload = {
        ...form,
        degreeStart: `${form.degreeStart.year}-${String(form.degreeStart.month + 1).padStart(2, "0")}`,
        degreeEnd: `${form.degreeEnd.year}-${String(form.degreeEnd.month + 1).padStart(2, "0")}`,
      };
      await axios.post("http://localhost:5000/api/submit", payload);
      setResponse("✅ Form submitted successfully!");
    } catch (err) {
      console.error(err);
      setResponse("❌ Failed to submit form");
    }
  };

  return (
    <div className="container mt-4">
      <h3 className="text-center mb-4">Student Registration Form</h3>

      <form onSubmit={handleSubmit} className="card p-4 shadow-lg">
        <div className="row g-3">
          {/* First and Last Name */}
          <div className="col-md-6">
            <label className="form-label">First Name</label>
            <input
              className="form-control"
              name="firstName"
              value={form.firstName}
              onChange={handleChange}
              placeholder="Enter first name"
              required
            />
          </div>

          <div className="col-md-6">
            <label className="form-label">Last Name</label>
            <input
              className="form-control"
              name="lastName"
              value={form.lastName}
              onChange={handleChange}
              placeholder="Enter last name"
              required
            />
          </div>

          {/* Email and Password */}
          <div className="col-md-6">
            <label className="form-label">Email</label>
            <input
              type="email"
              className="form-control"
              name="email"
              value={form.email}
              onChange={handleChange}
              placeholder="Enter email"
              required
            />
          </div>

          <div className="col-md-6">
            <label className="form-label">Password</label>
            <input
              type="password"
              className="form-control"
              name="password"
              value={form.password}
              onChange={handleChange}
              placeholder="Enter password"
              required
            />
          </div>

          {/* Student ID & Education */}
          <div className="col-md-6">
            <label className="form-label">Student ID</label>
            <input
              className="form-control"
              name="studentId"
              value={form.studentId}
              onChange={handleChange}
              placeholder="Enter student ID"
            />
          </div>

          <div className="col-md-6">
            <label className="form-label">Education</label>
            <input
              className="form-control"
              name="education"
              value={form.education}
              onChange={handleChange}
              placeholder="e.g. Bachelor of Engineering"
            />
          </div>

          {/* Major */}
          <div className="col-md-6">
            <label className="form-label">Major</label>
            <input
              className="form-control"
              name="major"
              value={form.major}
              onChange={handleChange}
              placeholder="e.g. Software Engineering"
            />
          </div>

          {/* Degree Duration */}
          <div className="col-md-6">
            <label className="form-label">Degree Start</label>
            <div className="d-flex align-items-center">
              <button
                type="button"
                className="btn btn-outline-secondary btn-sm me-2"
                onClick={() => changeMonth("degreeStart", -1)}
              >
                &lt;
              </button>
              <span className="flex-grow-1 text-center border rounded py-2">
                {months[form.degreeStart.month]} {form.degreeStart.year}
              </span>
              <button
                type="button"
                className="btn btn-outline-secondary btn-sm ms-2"
                onClick={() => changeMonth("degreeStart", 1)}
              >
                &gt;
              </button>
            </div>
          </div>

          <div className="col-md-6">
            <label className="form-label">Expected Graduation</label>
            <div className="d-flex align-items-center">
              <button
                type="button"
                className="btn btn-outline-secondary btn-sm me-2"
                onClick={() => changeMonth("degreeEnd", -1)}
              >
                &lt;
              </button>
              <span className="flex-grow-1 text-center border rounded py-2">
                {months[form.degreeEnd.month]} {form.degreeEnd.year}
              </span>
              <button
                type="button"
                className="btn btn-outline-secondary btn-sm ms-2"
                onClick={() => changeMonth("degreeEnd", 1)}
              >
                &gt;
              </button>
            </div>
          </div>

          {durationText && (
            <div className="col-12">
              <div className="alert alert-info py-2 mb-0">
                Degree Duration: <strong>{durationText}</strong>
              </div>
            </div>
          )}

          {/* Gender */}
          <div className="col-md-6">
            <label className="form-label d-block">Gender</label>
            <div className="d-flex">
              <div className="form-check me-3">
                <input
                  type="radio"
                  name="gender"
                  value="male"
                  checked={form.gender === "male"}
                  onChange={handleChange}
                  className="form-check-input"
                />
                <label className="form-check-label">Male</label>
              </div>
              <div className="form-check">
                <input
                  type="radio"
                  name="gender"
                  value="female"
                  checked={form.gender === "female"}
                  onChange={handleChange}
                  className="form-check-input"
                />
                <label className="form-check-label">Female</label>
              </div>
            </div>
          </div>
        </div>

        <div className="mt-4 text-center">
          <button className="btn btn-success px-5" type="submit">
            Submit
          </button>
        </div>
      </form>

      {response && (
        <p className="mt-3 text-center fw-bold text-primary">{response}</p>
      )}
    </div>
  );
}

export default StudentForm;
