import React, { useState } from "react";

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("user");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const submit = async (e) => {
    e.preventDefault();
    setError("");
    if (!username || !password) { setError("Username and password are required."); return; }
    setLoading(true);
    try {
      const res = await fetch("/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password, role }),
      });
      if (!res.ok) {
        const body = await res.json().catch(() => ({ message: "Login failed" }));
        setError(body.message || "Invalid credentials.");
        setLoading(false);
        return;
      }
      const data = await res.json();
      if (data.token) localStorage.setItem("authToken", data.token);
      window.location.href = data.redirect || (role === "admin" ? "/admin/dashboard" : "/user/dashboard");
    } catch (err) {
      setError("Network error. Try again.");
      setLoading(false);
      console.error(err);
    }
  };

  return (
    <main className="container">
      <form className="card" onSubmit={submit} id="loginForm" autoComplete="off">
        <h1>Sign in</h1>
        <label className="field"><span>Username</span>
          <input value={username} onChange={e => setUsername(e.target.value)} name="username" type="text" required />
        </label>
        <label className="field"><span>Password</span>
          <input value={password} onChange={e => setPassword(e.target.value)} name="password" type="password" required />
        </label>
        <fieldset className="roleField" onChange={e => setRole(e.target.value)}>
          <legend>Sign in as</legend>
          <label><input type="radio" name="role" value="user" defaultChecked /> User</label>
          <label><input type="radio" name="role" value="admin" /> Admin</label>
        </fieldset>
        <div id="error" className="error" aria-live="polite">{error}</div>
        <button id="submitBtn" className="primary" type="submit" disabled={loading}>{loading ? "Signing in…" : "Sign in"}</button>
        <div className="help"><small id="roleNote">Signing in as <strong>{role === "admin" ? "Admin" : "User"}</strong>.</small></div>
      </form>
    </main>
  );
}
