import React, { useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function StudentForm() {
  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    studentId: "",
    education: "",
    major: "",
    degreeStart: new Date("2021-01-01"),
    degreeEnd: new Date("2025-12-01"),
    gender: "",
  });

  const [response, setResponse] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = {
        ...form,
        degreeStart: form.degreeStart.toISOString().split("T")[0],
        degreeEnd: form.degreeEnd.toISOString().split("T")[0],
      };

      await axios.post("http://localhost:5000/api/submit", payload);
      setResponse("✅ Form submitted successfully!");
    } catch (err) {
      console.error(err);
      setResponse("❌ Failed to submit form");
    }
  };

  return (
    <div className="container mt-4">
      <h3 className="text-center mb-4">Student Registration Form</h3>

      <form onSubmit={handleSubmit} className="card p-4 shadow-lg">
        <div className="row g-3">
          {/* First & Last Name */}
          <div className="col-md-6">
            <label className="form-label">First Name</label>
            <input
              className="form-control"
              name="firstName"
              value={form.firstName}
              onChange={handleChange}
              placeholder="Enter first name"
              required
            />
          </div>
          <div className="col-md-6">
            <label className="form-label">Last Name</label>
            <input
              className="form-control"
              name="lastName"
              value={form.lastName}
              onChange={handleChange}
              placeholder="Enter last name"
              required
            />
          </div>

          {/* Email & Password */}
          <div className="col-md-6">
            <label className="form-label">Email</label>
            <input
              type="email"
              className="form-control"
              name="email"
              value={form.email}
              onChange={handleChange}
              placeholder="Enter email"
              required
            />
          </div>
          <div className="col-md-6">
            <label className="form-label">Password</label>
            <input
              type="password"
              className="form-control"
              name="password"
              value={form.password}
              onChange={handleChange}
              placeholder="Enter password"
              required
            />
          </div>

          {/* Student ID & Education */}
          <div className="col-md-6">
            <label className="form-label">Student ID</label>
            <input
              className="form-control"
              name="studentId"
              value={form.studentId}
              onChange={handleChange}
              placeholder="Enter student ID"
            />
          </div>
          <div className="col-md-6">
            <label className="form-label">Education</label>
            <input
              className="form-control"
              name="education"
              value={form.education}
              onChange={handleChange}
              placeholder="e.g. Bachelor of Engineering"
            />
          </div>

          {/* Major */}
          <div className="col-md-6">
            <label className="form-label">Major</label>
            <input
              className="form-control"
              name="major"
              value={form.major}
              onChange={handleChange}
              placeholder="e.g. Software Engineering"
            />
          </div>

          {/* Degree Start & End Dates (Calendar) */}
          <div className="col-md-6">
            <label className="form-label">Degree Start</label>
            <DatePicker
              selected={form.degreeStart}
              onChange={(date) => setForm({ ...form, degreeStart: date })}
              dateFormat="MMM yyyy"
              showMonthYearPicker
              showFullMonthYearPicker
              className="form-control"
              placeholderText="Select start date"
            />
          </div>
          <div className="col-md-6">
            <label className="form-label">Expected Graduation</label>
            <DatePicker
              selected={form.degreeEnd}
              onChange={(date) => setForm({ ...form, degreeEnd: date })}
              dateFormat="MMM yyyy"
              showMonthYearPicker
              showFullMonthYearPicker
              className="form-control"
              placeholderText="Select graduation date"
            />
          </div>

          {/* Gender */}
          <div className="col-md-6">
            <label className="form-label d-block">Gender</label>
            <div className="d-flex">
              <div className="form-check me-3">
                <input
                  type="radio"
                  name="gender"
                  value="male"
                  checked={form.gender === "male"}
                  onChange={handleChange}
                  className="form-check-input"
                />
                <label className="form-check-label">Male</label>
              </div>
              <div className="form-check">
                <input
                  type="radio"
                  name="gender"
                  value="female"
                  checked={form.gender === "female"}
                  onChange={handleChange}
                  className="form-check-input"
                />
                <label className="form-check-label">Female</label>
              </div>
            </div>
          </div>
        </div>

        {/* Submit Button */}
        <div className="mt-4 text-center">
          <button className="btn btn-success px-5" type="submit">
            Submit
          </button>
        </div>
      </form>

      {response && (
        <p className="mt-3 text-center fw-bold text-primary">{response}</p>
      )}
    </div>
  );
}

export default StudentForm;
