import React, { useState, useMemo } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

function StudentForm() {
  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    studentId: "",
    education: "",
    major: "",
    degreeStart: "",
    degreeEnd: "",
    gender: "",
  });

  const [response, setResponse] = useState("");
  const [dateError, setDateError] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === "degreeStart" || name === "degreeEnd") {
      const next = { ...form, [name]: value };
      if (next.degreeStart && next.degreeEnd && next.degreeEnd < next.degreeStart) {
        setDateError("Expected graduation cannot be before degree start.");
      } else {
        setDateError("");
      }
      setForm(next);
      return;
    }

    setForm({ ...form, [name]: value });
  };

  // Calculate duration between start and end date
  const durationText = useMemo(() => {
    if (!form.degreeStart || !form.degreeEnd || form.degreeEnd < form.degreeStart) return "";
    const [sY, sM] = form.degreeStart.split("-").map(Number);
    const [eY, eM] = form.degreeEnd.split("-").map(Number);
    const totalMonths = (eY - sY) * 12 + (eM - sM);
    const years = Math.floor(totalMonths / 12);
    const months = totalMonths % 12;
    const y = years > 0 ? `${years} year${years > 1 ? "s" : ""}` : "";
    const m = months > 0 ? `${months} month${months > 1 ? "s" : ""}` : "";
    return [y, m].filter(Boolean).join(" ");
  }, [form.degreeStart, form.degreeEnd]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (dateError) return;
    try {
      await axios.post("http://localhost:5000/api/submit", form);
      setResponse("✅ Form submitted successfully!");
      setForm({
        firstName: "",
        lastName: "",
        email: "",
        password: "",
        studentId: "",
        education: "",
        major: "",
        degreeStart: "",
        degreeEnd: "",
        gender: "",
      });
    } catch (err) {
      console.error(err);
      setResponse("❌ Failed to submit form");
    }
  };

  return (
    <div className="container mt-4">
      <h3 className="text-center mb-4">Student Registration Form</h3>

      <form onSubmit={handleSubmit} className="card p-4 shadow-lg">
        <div className="row g-3">
          {/* First Name */}
          <div className="col-md-6">
            <label className="form-label">First Name</label>
            <input
              className="form-control"
              name="firstName"
              value={form.firstName}
              onChange={handleChange}
              placeholder="Enter first name"
              required
            />
          </div>

          {/* Last Name */}
          <div className="col-md-6">
            <label className="form-label">Last Name</label>
            <input
              className="form-control"
              name="lastName"
              value={form.lastName}
              onChange={handleChange}
              placeholder="Enter last name"
              required
            />
          </div>

          {/* Email */}
          <div className="col-md-6">
            <label className="form-label">Email</label>
            <input
              type="email"
              className="form-control"
              name="email"
              value={form.email}
              onChange={handleChange}
              placeholder="Enter email"
              required
            />
          </div>

          {/* Password */}
          <div className="col-md-6">
            <label className="form-label">Password</label>
            <input
              type="password"
              className="form-control"
              name="password"
              value={form.password}
              onChange={handleChange}
              placeholder="Enter password"
              required
            />
          </div>

          {/* Student ID */}
          <div className="col-md-6">
            <label className="form-label">Student ID</label>
            <input
              className="form-control"
              name="studentId"
              value={form.studentId}
              onChange={handleChange}
              placeholder="Enter student ID"
            />
          </div>

          {/* Education */}
          <div className="col-md-6">
            <label className="form-label">Education</label>
            <input
              className="form-control"
              name="education"
              value={form.education}
              onChange={handleChange}
              placeholder="e.g. Bachelor of Engineering"
            />
          </div>

          {/* Major */}
          <div className="col-md-6">
            <label className="form-label">Major</label>
            <input
              className="form-control"
              name="major"
              value={form.major}
              onChange={handleChange}
              placeholder="e.g. Software Engineering"
            />
          </div>

          {/* Degree Duration */}
          <div className="col-md-6">
            <label className="form-label">Degree Start (Month/Year)</label>
            <input
              type="month"
              className="form-control"
              name="degreeStart"
              value={form.degreeStart}
              onChange={handleChange}
              min="2010-01"
              max="2035-12"
            />
          </div>

          <div className="col-md-6">
            <label className="form-label">Expected Graduation (Month/Year)</label>
            <input
              type="month"
              className="form-control"
              name="degreeEnd"
              value={form.degreeEnd}
              onChange={handleChange}
            />
          </div>

          {dateError && (
            <div className="col-12">
              <div className="alert alert-danger py-2 mb-0">{dateError}</div>
            </div>
          )}
          {!dateError && durationText && (
            <div className="col-12">
              <div className="alert alert-info py-2 mb-0">
                Duration: <strong>{durationText}</strong>
              </div>
            </div>
          )}

          {/* Gender */}
          <div className="col-md-6">
            <label className="form-label d-block">Gender</label>
            <div className="d-flex">
              <div className="form-check me-3">
                <input
                  type="radio"
                  name="gender"
                  value="male"
                  checked={form.gender === "male"}
                  onChange={handleChange}
                  className="form-check-input"
                />
                <label className="form-check-label">Male</label>
              </div>
              <div className="form-check">
                <input
                  type="radio"
                  name="gender"
                  value="female"
                  checked={form.gender === "female"}
                  onChange={handleChange}
                  className="form-check-input"
                />
                <label className="form-check-label">Female</label>
              </div>
            </div>
          </div>
        </div>

        <div className="mt-4 text-center">
          <button className="btn btn-success px-5" type="submit" disabled={!!dateError}>
            Submit
          </button>
        </div>
      </form>

      {response && (
        <p className="mt-3 text-center fw-bold text-primary">{response}</p>
      )}
    </div>
  );
}

export default StudentForm;
